        rehberler.setOnGroupExpandListener(new ExpandableListView.OnGroupExpandListener() {
            public void onGroupExpand(int groupPosition) {

                int len = new Islemler(rehber.this).getGroupCount();
                for (int i = 0; i < len; i++) {
                    if (i != groupPosition) {
                        rehberler.collapseGroup(i);
                    }
                }
            }
        });

